class Solution:
    def minMoves(nums: list[int]) -> int:
        count = 0
        
        nums = list(set(nums))
        leng = len(nums)
        while len(set(nums))!=1:
            nums.sort()
            for i in range(leng-1):
                nums[i]+=1
            count+=1
        return count
    def minMoves2(nums: list[int]) -> int:##this code is faster
        min_num = min(nums)
        moves = sum(nums) - len(nums) * min_num
        return moves
